const Home = ({ initialImageIndex = null }) => {
    const [isFullScreen, setIsFullScreen] = useState(false);
    const [selectedImage, setSelectedImage] = useState('');
    const images = Array.from({ length: 7 }, (_, index) => `/images/${index + 1}.jpg`);
  
    useEffect(() => {
      if (initialImageIndex !== null) {
        setSelectedImage(images[initialImageIndex]);
        setIsFullScreen(true);
      }
    }, [initialImageIndex, images]);
  
    const handleImageClick = (src) => {
      if (isFullScreen && selectedImage === src) {
        setIsFullScreen(false);
      } else {
        setSelectedImage(src);
        setIsFullScreen(true);
      }
    };
  
    return (
      <>
        <ImageSlider
          images={images}
          isFullScreen={isFullScreen}
          selectedImage={selectedImage}
          onImageClick={handleImageClick}
        />
      </>
    );
  };
  
  export default Home;
