import React, { useEffect, useRef, useState } from 'react';
import { motion, AnimatePresence, LayoutGroup } from 'framer-motion';
import './ImageSliderStyles.css';

const ImageSlider = ({ startFullScreen = false, initialImage = null }) => {
    const trackRef = useRef(null);
    const [isFullScreen, setIsFullScreen] = useState(startFullScreen);
    const [selectedImage, setSelectedImage] = useState(initialImage);
    const [selectedIndex, setSelectedIndex] = useState(null);
    const [imageTransitionState, setImageTransitionState] = useState(null);

    const [sliderState, setSliderState] = useState({
        mouseDownAt: 0,
        prevPercentage: 0,
        percentage: 0
    });

    useEffect(() => {
        if (!selectedImage && trackRef.current) {
            const firstImage = trackRef.current.querySelector('.image');
            if (firstImage) {
                const rect = firstImage.getBoundingClientRect();
                const computedStyle = window.getComputedStyle(firstImage);
                const objectPosition = computedStyle.objectPosition;

                setImageTransitionState({
                    rect: { top: rect.top, left: rect.left, width: rect.width, height: rect.height },
                    objectPosition,
                    scale: 1
                });

                const simulatedClickEvent = new MouseEvent('click', {
                    bubbles: true,
                    cancelable: true,
                    clientX: rect.left + rect.width / 2,
                    clientY: rect.top + rect.height / 2,
                });
                firstImage.dispatchEvent(simulatedClickEvent);
            }
        }
    }, [selectedImage]);

    useEffect(() => {
        const track = trackRef.current;
        if (!track) return;

        const handleOnDown = (e) => {
            if (isFullScreen) return;
            const clientX = e.clientX || (e.touches && e.touches[0]?.clientX);
            setSliderState(prev => ({ ...prev, mouseDownAt: clientX }));
        };

        const handleOnUp = () => {
            if (isFullScreen) return;
            setSliderState(prev => ({ ...prev, mouseDownAt: 0, prevPercentage: prev.percentage }));
        };

        const handleOnMove = (e) => {
            if (isFullScreen) return;
            const clientX = e.clientX || (e.touches && e.touches[0]?.clientX);
            if (sliderState.mouseDownAt === 0) return;

            const mouseDelta = sliderState.mouseDownAt - clientX;
            const maxDelta = window.innerWidth / 0.8;
            const percentage = (mouseDelta / maxDelta) * -100;
            const nextPercentageUnconstrained = sliderState.prevPercentage + percentage;
            const nextPercentage = Math.max(Math.min(nextPercentageUnconstrained, 0), -95);

            setSliderState(prev => ({ ...prev, percentage: nextPercentage }));

            track.animate({ transform: `translate(${nextPercentage}%, -50%)` }, { duration: 600, fill: 'forwards', easing: 'cubic-bezier(0.25, 0.1, 0.25, 1)' });

            const images = track.getElementsByClassName("image");
            Array.from(images).forEach((image) => {
                image.animate({ objectPosition: `${100 + nextPercentage * 1.1}% center` }, { duration: 600, fill: 'forwards', easing: 'cubic-bezier(0.25, 0.1, 0.25, 1)' });
            });
        };

        window.addEventListener('mousedown', handleOnDown);
        window.addEventListener('mousemove', handleOnMove);
        window.addEventListener('mouseup', handleOnUp);
        window.addEventListener('touchstart', handleOnDown);
        window.addEventListener('touchmove', handleOnMove);
        window.addEventListener('touchend', handleOnUp);

        return () => {
            window.removeEventListener('mousedown', handleOnDown);
            window.removeEventListener('mousemove', handleOnMove);
            window.removeEventListener('mouseup', handleOnUp);
            window.removeEventListener('touchstart', handleOnDown);
            window.removeEventListener('touchmove', handleOnMove);
            window.removeEventListener('touchend', handleOnUp);
        };
    }, [isFullScreen, sliderState]);

    const handleImageClick = (e, src, index) => {
        const imgElement = e.target;
        const rect = imgElement.getBoundingClientRect();
        const computedStyle = window.getComputedStyle(imgElement);
        const objectPosition = computedStyle.objectPosition;

        setImageTransitionState({
            rect: { top: rect.top, left: rect.left, width: rect.width, height: rect.height },
            objectPosition,
            scale: 1
        });
        setSelectedImage(src);
        setSelectedIndex(index);
        setIsFullScreen(true);
    };

    const handleFullScreenClose = () => {
        setIsFullScreen(false);
        setSelectedImage(null);
        setImageTransitionState(null);
    };

    const images = Array.from({ length: 7 }, (_, index) => `/images/${index + 1}.jpg`);

    return (
        <LayoutGroup>
            <div
                id="image-track"
                ref={trackRef}
                style={{
                    transform: `translate(${sliderState.percentage}%, -50%)`,
                    opacity: isFullScreen ? 0 : 1,
                    transition: 'opacity 0.4s ease-in-out'
                }}
            >
                {images.map((src, index) => (
                    <motion.img
                        layoutId={`image-${index}`}
                        key={src}
                        className="image"
                        src={src}
                        alt={`Image ${index + 1}`}
                        draggable="false"
                        onClick={(e) => handleImageClick(e, src, index)}
                        whileHover={{ scale: 1.05 }}
                        style={{ cursor: 'pointer', willChange: 'transform' }}
                    />
                ))}
            </div>

            <AnimatePresence>
                {isFullScreen && imageTransitionState && (
                    <motion.div
                        key={selectedImage}
                        initial={{
                            position: 'fixed',
                            top: imageTransitionState.rect.top,
                            left: imageTransitionState.rect.left,
                            width: imageTransitionState.rect.width,
                            height: imageTransitionState.rect.height,
                            scale: imageTransitionState.scale,
                            backgroundColor: 'black',
                            zIndex: 1000,
                            overflow: 'hidden'
                        }}
                        animate={{ top: 0, left: 0, width: '100vw', height: '100vh', scale: 1 }}
                        exit={{
                            top: imageTransitionState.rect.top,
                            left: imageTransitionState.rect.left,
                            width: imageTransitionState.rect.width,
                            height: imageTransitionState.rect.height,
                            scale: imageTransitionState.scale
                        }}
                        transition={{ duration: 0.4, ease: "easeInOut" }}
                        onClick={handleFullScreenClose}
                        style={{ position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh', zIndex: 1000, backgroundColor: 'black', overflow: 'hidden' }}
                    >
                        <motion.img
                            src={selectedImage}
                            style={{ width: '100%', height: '100%', objectFit: 'cover', objectPosition: imageTransitionState.objectPosition }}
                            alt="Full screen view"
                        />
                    </motion.div>
                )}
            </AnimatePresence>
        </LayoutGroup>
    );
};

export default ImageSlider;
