import React, { useState } from 'react';
import IntroAnimation from '../../components/Intro/IntroAnimation';
import ImageSlider from '../../components/ImageSlider/ImageSlider';

const Home = () => {
    const [showIntro, setShowIntro] = useState(true);
    const [showSlider, setShowSlider] = useState(false);
    const [startFullScreen, setStartFullScreen] = useState(false);

    const handleAnimationEnd = () => {
        // Animation has completed, prepare for slider
        setStartFullScreen(true);
    };

    const handleFullScreenReady = () => {
        // Intro animation is done, show slider
        setShowSlider(true);
        // Hide intro after a brief delay
        setTimeout(() => {
            setShowIntro(false);
        }, 500);
    };

    return (
        <div style={{ position: 'relative', width: '100vw', height: '100vh' }}>
            {showIntro && (
                <IntroAnimation 
                    onAnimationEnd={handleAnimationEnd}
                    onFullScreenReady={handleFullScreenReady}
                />
            )}
            {showSlider && (
                <ImageSlider 
                    startFullScreen={startFullScreen}
                    initialImage="/images/1.jpg"
                />
            )}
        </div>
    );
};

export default Home;
